import React, { Component } from 'react'
import './BurgerStyle.css'


export default class Burger extends Component{
  state = {
    lettuce: 0,
    tomato: 0,
    cheese: 0,
    meat:0
  }

  addRemoveIngredient = (action, ingredient) =>{
    let {
      lettuce,
      cheese,
      meat,
      tomato
    } = this.state

    let stateValue;

    switch (ingredient) {
      case 'lettuce':{
          stateValue = lettuce
        break;
      }
      case 'cheese':{
          stateValue = cheese
        break;
      }
      case 'meat':{
          stateValue = meat
        break;
      }
      case 'tomato':{
          stateValue = tomato
        break;
      }
      default: break;
    }


    if (action === 'add') {
      stateValue = stateValue + 1
    }else {
      stateValue = stateValue - 1
    }


    this.setState({
      [ingredient]: stateValue >= 0 ? stateValue : 0
    })

  }

  burgerContent = () => {
    let {
      lettuce,
      cheese,
      meat,
      tomato
    } = this.state

    let burger = []

    for (let i = 0; i < lettuce; i++){
      burger.push(<div key={burger.length} className="lettuseSide"></div>);
    }

    for (let i = 0; i < lettuce; i++){
      burger.push(<div key={burger.length} className="lettuseSide"></div>);
    }


  }

  render(){
    return(
      <>
        <div className="burgerIngredients">
          <div className="topSide"></div>
            content
          <div className="bottomSide">

          </div>
        </div>
        <div className="ingredientsBlock">
          <p>Lettuce</p>
          <div className="ingrBtns">
              <button className="ingrBtn" onClick={() => this.addRemoveIngredient('add','lettuce')}>Add</button>
              <button className="ingrBtn" onClick={() => this.addRemoveIngredient('remove','lettuce')}>Remove</button>
          </div>
          <p>Tomato</p>
          <div className="ingrBtns">
              <button className="ingrBtn" onClick={() => this.addRemoveIngredient('add','lettuce')}>Add</button>
              <button className="ingrBtn" onClick={() => this.addRemoveIngredient('remove','lettuce')}>Remove</button>
          </div>
          <p>cheese</p>
          <div className="ingrBtns">
              <button className="ingrBtn" onClick={() => this.addRemoveIngredient('add','lettuce')}>Add</button>
              <button className="ingrBtn" onClick={() => this.addRemoveIngredient('remove','lettuce')}>Remove</button>
          </div>
          <p>Meat</p>
          <div className="ingrBtns">
              <button className="ingrBtn" onClick={() => this.addRemoveIngredient('add','lettuce')}>Add</button>
              <button className="ingrBtn" onClick={() => this.addRemoveIngredient('remove','lettuce')}>Remove</button>
          </div>
        </div>
      </>
    )
  }

}
